INSERT INTO `abnmt_theater_people` (`id`, `title`, `slug`, `family_name`, `given_name`, `bio`, `grade`, `state`, `gender`, `published`, `created_at`, `updated_at`) VALUES
(1, 'Александр Гончарук', 'aleksandr-goncharuk', 'Гончарук', 'Александр', NULL, NULL, 'cooperate', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(2, 'Алексей Исполатов', 'aleksei-ispolatov', 'Исполатов', 'Алексей', '<p>Петербургский актер, режиссер. В 1977 году окончил ЛГУ (Болгарская Филология), а в 1985 году Ленинградский Государственный Институт Театра, Музыки и Кинематографии, факультет режиссура драмы. 1984-1985 г. работал в Норильском драматическом театре, 1985-1987 г. — в Новосибирском ТЮЗе, 1987-1989 г. — в Пермском театре драмы. Предпочтение в своей работе отдает созданию спектаклей для детей, и это не случайно — в ранний период своей творческой деятельности А.Н. Исполатов немало сил вложил в эстетическое воспитание младшего поколения, работая в художественной самодеятельности. Список его режиссерских работ разнообразен => «Аленький цветочек», «Бумажный патефон», мюзикл «День рождения кота Леопольда», «Серебряное копытце», «Золушка» Е.Шварца и «Неожиданный случай» К.Гольдони.</p>\n<p>Поставил целый ряд спектаклей в театре «Комедианты» => в 1991 г. <a href="http =>//komedianty.abnmt.com/performance/ekhala-derevnya-mimo-muzhika" title="Ехала деревня мимо мужика">«Ехала деревня мимо мужика»</a> (по мотивам русских народных сказок), <a href="http =>//komedianty.abnmt.com/performance/provintsialnye-anekdoty" title="Провинциальные анекдоты">«Провинциальные анекдоты»</a> (А. Вампилов), в 1996 г. «Сказка о четырёх близнецах» (П. Панчев), в 2004 году — «Все кошки серы» (А. Образцов), в 2000 г. «Питер Пэн» (мюзикл по сказке Дж. Браун), в 2009 <a href="http =>//komedianty.abnmt.com/performance/kytsik-mytsik-i-tyotushka-mari" title="Кыцик, Мыцик и тётушка Мари">«Кыцик, Мыцик и тётушка Мари»</a> по Е. Чеповецкому, и последняя громкая премьера для ребят школьного возраста <a href="http =>//komedianty.abnmt.com/performance/uchilka-iz-buduschego" title="Училка из будущего">«Училка из будущего»</a> по пьесе В.Ольшанского «Училка XXI века».</p>\n<p>В Санкт-Петербургском государственном драматическом театре «Комедианты» работает практически со дня его основания (c 1989 г.).</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(3, 'Алёна Азарова', 'alyona-azarova', 'Азарова', 'Алёна', '<p>Родилась 4 апреля 1988 года в г. Лодейное поле. Окончила детскую театральную школу и играла в Детском Образцовом Драматическом театре «Антрэ». В 2005 году поступила в СПбГАТИ на курс И. А. Зайкина. С 2009 по 2010 гг. играла в Лодейнопольском Драматическом театре.</p>\n<p>С 2010 года актриса Санкт-Петербургского государственного драматического театра «Комедианты», на его сцене дебютировала в роли Татьяны-прачки в спектакле «МуМу». Лауреат специального приза экспертного совета высшей театральной премии «Золотой софит» сезона 2010-2011, за «Лучший дебют» в спектакле <a href="http =>//komedianty.abnmt.com/performance/krepostnaya-lyubov-mumu" title="Крепостная любовь (Муму)">«МуМу»</a>.</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(4, 'Александр Шевелёв', 'aleksandr-shevelyov', 'Шевелёв', 'Александр', '<p>Родился 5 сентября 1948 года.</p>\n<p>Окончил Саратовское театральное училище имени И. А. Слонова (педагог — В. И. Давыдов).</p>\n<p>Играл в Ленинградском ТЮЗе.</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(5, 'Анастасия Пижель', 'anastasiya-pizhel', 'Пижель', 'Анастасия', NULL, NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(6, 'Анатолий Ильченко', 'anatolii-ilchenko', 'Ильченко', 'Анатолий', '<p>Родился 14 июня 1976 года в г. Ноябрьске. В 1998 году окончил Санкт-Петербургскую Академию театрального искусства, класс профессора А. С. Шведерского.</p>\n<p>С 2004 года актёр Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(7, 'Андрей Шимко', 'andrei-shimko', 'Шимко', 'Андрей', '<p>Родился 22 августа 1969 года. В 1996 году окончил театральный институт «Школа Русской драмы», курс заслуженного артиста России Семёна Сытника. Играет во многих театрах г. Санкт-Петербурга (Театр Малыщицкого, Молодёжный театр на фонтанке, Александринский театр, Приют Комедианта и т.д. ) С 1997 года по 2005 года работал в труппе Санкт-Петербургского государственного драматического театра «Комедианты». Преподаёт актёрское мастерство в театральном институте «Школа Русской драмы», имеет свой курс.</p>\n<p>С 2005 года сотрудничает с Санкт-Петербургским государственным драматическим театром «Комедианты».</p>', NULL, 'cooperate', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(8, 'Андрей Вергелис', 'andrei-vergelis', 'Вергелис', 'Андрей', NULL, NULL, 'cooperate', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(9, 'Анна Загребна', 'anna-zagrebna', 'Загребна', 'Анна', '<p>Родилась 16 июля 1976 года, в маленьком волжском городе. 3,5 года в Ярославской театральной академии на кукольном факультете укрепили невозможное желание ехать в Питер. В 2000 году закончила с красным дипломом СПбГАТИ, курс заслуженного деятеля искусств России Михаила Левшина. В 1999 году работала в театре — бенефис М. Боярского, театр «Суббота», 2000-2003 гг. театр «ЛЬвёнок», с 2001 по 2004 год в театре «Встреча». 2008 год театр русской драмы. 2002-2005 гг. преподавала в модельном агентстве актерское мастерство и сценическое движение. С 2000 по 2005 год выступления в составе ансамбля Фламенко Л.Фадеевой.</p>\n<p>С 1998 года актриса Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(10, 'Екатерина Белова', 'ekaterina-belova', 'Белова', 'Екатерина', '<p>Родилась 13 октября 1982 года в Ленинграде. Окончила СПбГАТИ, курс В. В. Петрова.</p>\n<p>С 2004 года актриса Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(11, 'Екатерина Культина', 'ekaterina-kultina', 'Культина', 'Екатерина', '<p>Родилась 2 ноября 1983 года в Ленинграде. В 2004 году окончила СПбГАТИ (мастерская В.В.Петрова). После окончиния учёбы с 2004 года актриса Санкт-Петербургского Драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(12, 'Елизавета Чернова', 'elizaveta-chernova', 'Чернова', 'Елизавета', '<p>Родилась 17 ноября 1991 года в г. Санкт-Петербург. В 2005 году закончила детскую театральную студию при Санкт-Петербургском театре &quot;Зазеркалье&quot;.В 2008 году закончила детскую театральную студию &quot;Дуэт&quot;.В 2012 году закончила Санкт-Петербургский Государственный Университет,кафедра &quot;театрального искусства&quot;,мастерская нар.артиста России В.В.Лобанова.</p>\n<p>С 2012 года занята в спектаклях Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(13, 'Евгений Баранов', 'evgenii-baranov', 'Баранов', 'Евгений', NULL, 'Заслуженный артист России', 'cooperate', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(14, 'Евгений Талашманов', 'evgenii-talashmanov', 'Талашманов', 'Евгений', NULL, NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(15, 'Филипп Азаров', 'filipp-azarov', 'Азаров', 'Филипп', '<p>Родился 24 марта 1983 года. Окончил СПбГАТИ в 2004 году, курс В.В. Петрова.</p>\n<p>С 2004 года актер Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(16, 'Галина Сабурова', 'galina-saburova', 'Сабурова', 'Галина', '<p>Родилась 22 декабря 1937 года в Ленинграде. В 1964 году окончила ЛГИТМиК (СПБГАТИ) курс Л.Макарьева, Ф.М.Никитина. В 1964-1969 годах работала в Красноярском драматическом театре. В 1970-1975 гг. — в &quot;Ленконцерте&quot;. В 1976-1984 гг. — на Ленфильме. В 1984-1998 гг. — в &quot;Студии киноактёра&quot;. Актриса прошла большой творческий путь, много снималась в кино у таких режиссеров, как Иосиф Хейфиц, Виктор Садовский, Владимир Фетин, Эрнест Ясан, Виктор Титов, Александр Бурцев, Александр Калягин.</p>\n<p>С 1999 года актриса Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(17, 'Ильгиз Булгаков', 'ilgiz-bulgakov', 'Булгаков', 'Ильгиз', NULL, NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(18, 'Илья Безрук', 'ilya-bezruk', 'Безрук', 'Илья', '<p>Родился 8 февраля 1986 года в городе Норильске. В 2007 году окончил Красноярскую Государственную Академию Музыки и Театра. Мастер курса — Ирина Борисовна Калиновская. С 2007 по 2008 год работал в Новгородском Академическом театре драмы имени Ф.М.ДОСТОЕВСКОГО. С 2008 по 2011 год работал в Ленинградском Областном Государственном Театре Юного Зрителя (ЛОГУК ТЮЗ).</p>\n<p>В 2011 году принят в труппу Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(19, 'Геннадий Спириденков', 'gennadii-spiridenkov', 'Спириденков', 'Геннадий', '<p>Родился в 1956-м году. Окончил ЛГИТМиК, факультет драматического искусства (1981), педагог — З. Я. Корогодский. Работал в Санкт-Петербургском Театре юного зрителя им. А.А.Брянцева. Заслуженный артист Российской Федерации (1997г). Артист разговорного жанра &quot;Петербург-концерта&quot;.</p>\n<p>В Санкт-Петербургском государственном драматическом театре «Комедианты» с 2007 года.</p>', 'Заслуженный артист России', 'graduate', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(20, 'Ирина Митюшкина', 'irina-mityushkina', 'Митюшкина', 'Ирина', '<p>Родилась 15 июня 1964 года в г. Магнитогорске. В 1988 году окончила ГАКИ, факультет театральной режиссуры, педагог — Л.А. Ящинина. Работала в Уральском муниципальном театре драмы.</p>\n<p>С 2005 года актриса Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(21, 'Лариса Климова', 'larisa-klimova', 'Климова', 'Лариса', '<p>Родилась 4 октября 1969 года в Воронеже. В 1995 году окончила Институт искусств (Воронеж), факультет театрального искусства , мастер Сисикина И.Б. 1992-1995 г. играла в Воронежском Молодежном театре, 1995-1997 г. в Санкт-Петербургском театре «Причастие».</p>\n<p>С 1997 года актриса Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(22, 'Кирилл Датешидзе', 'kirill-dateshidze', 'Датешидзе', 'Кирилл', '<p>Родился 22 сентября 1946 года. В 1972 году закончил Ленинградский Государственный институт театра, музыки и кинематографии. Играет в разных театрах г. Санкт-Петербурга (Антреприза Миронова, Театр им.Акимова, Приют Комедианта и т.д.).</p>\n<p>С 2008 года сотрудничает с Санкт-Петербургским государственным драматическим театром «Комедианты».</p>', NULL, 'cooperate', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(23, 'Леонид Зябкин', 'leonid-zyabkin', 'Зябкин', 'Леонид', '<p>Родился 31 декабря 1983 года в Ленинграде. В 2008 году окончил БИЭПП , театральный факультет, мастерская Народного артиста России Петра Вельяминова. В 2008 году окончил музыкальную школу им.Римского-Корсакого по классу гитары. Дипломант фестиваля авторской песни &quot;ТОПОС&quot;. С 2008 гитарист рок-группы &quot;Где-то рядом&quot;.</p>\n<p>В 2008 году принят в труппу Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(24, 'Лина Сорокина', 'lina-sorokina', 'Сорокина', 'Лина', NULL, NULL, 'cooperate', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(25, 'Людмила Никитина', 'lyudmila-nikitina', 'Никитина', 'Людмила', '<p>Родилась 23 апреля 1970 года. В 1989 году, в городе Львове, окончила Училище культуры, режиссерский факультет. В 1996 году окончила Санкт-Петербургскую Академию Культуры, режиссерский факультет, класс профессора Н. И. Басина. А в 2002 году — Аспирантуру Санкт-Петербургской Государственной Академии Театрального Искусства, факультет педагогики актерского мастерства, класс заслуженного деятеля искусств М. А. Левшина.</p>\n<p>В городе Львове руководила детской театральной студией. 1996 год — преподавание в с.ш. № 16 пластики и актерского мастерства, г. Великий Новгород. 1999-2001 год — педагог на курсе актерского мастерства СПбГАТИ, класс М. А. Левшина. C 2005 года преподаю СПбГУКИ — педагог режиссуры и актерского мастерства на кафедре «Режиссуры и продюсирования театрализованных шоу программ». 2000- 2003 гг. — христианское &quot;Радио Мария&quot; — ведущая передачи &quot;Трамвайчик&quot; в цикле вечерних детских программ.</p>\n<p>Создание и продюсирование антрепризных проектов. С 2003 года — организатор проводимого театром «Комедианты» благотворительного фестиваля для детей-сирот Ленинградской области «Дворцы Санкт-Петеребурга — детям». А с 2006 года является директором Автономной Некоммерческой Организации «Центр реализации творческих проектов «Адмиралтейский».</p>\n<p>С 2008 года организатор и член жюри Международных фестивалей творческих коллективов, проводимых в разных городах России и зарубежья.</p>\n<p>В 1997 актриса Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(26, 'Мария Осипова', 'mariya-osipova', 'Осипова', 'Мария', '<p>Родилась 10 октября 1991 года в Ужгороде. В 2012 году закончила Самарскую государственную академию культуры и искусств, мастерская Гончарова В.Н. и Болотова А.И. Имеется диплом российской национальной театральной премии фестиваля театрального искусства для детей &quot;Арлекин&quot;(2012), сотрудничает с Лабораторией ON.ТЕАТР.</p>\n<p>С 2013 года занята в спектаклях Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'cooperate', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(27, 'Максим Сергеев', 'maksim-sergeev', 'Сергеев', 'Максим', '<p>Родился 21 марта 1968 года. В 1991 году окончил ЛГИТМиК (СПбГАТИ), драматического искусства , педагог — Петров В.В. Имеет дополнительное образование — ассистентура СПбГАТИ, кафедра речи. Активно снимается в кино и принимает участие в дубляже зарубежных фильмов. Знаменитый голос «Микки Мауса» в России (Disney). Озвучание фильмов и мультфильмов => «Тачки» (2006), «Сезон охоты 2» (2008), «Три икса 2» (2005), «Сокровище нации» (2004), «Человек паук 2» (2004), «Послезавтра» (2004), «Плохие парни 2» (2003), «Пункт назначения» (2000), &quot;Люди в черном 3&quot;.</p>\n<p>С 1990 года актер Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(28, 'Михаил Левшин', 'mikhail-levshin', 'Левшин', 'Михаил', '<p>М. А. Левшин с первых своих спектаклей заявил о себе, как о самостоятельной и самобытной режиссёрской индивидуальности. До создания областного драматического театра «Комедианты» — главного дела своей жизни, Михаил Левшин прошел большой путь, ставя спектакли на сценах разных петербургских театров.</p>\n<p>Творческая программа М. А. Левшина, как режиссёра, соединила в себе новаторский поиск в постановочных средствах, мизансценических решениях с развитием идей и традиций российского актёрского театра, где все выразительные средства сценического искусства аккомпанируют главному => актёрскому перевоплощению, психологической разработке роли, живому, действенному способу существования.</p>\n<p>Важнейшей страницей творческой биографии Левшина М. А. стало создание нового театра — ныне Ленинградское областное государственное бюджетное учреждение культуры Драматический театр «Комедианты». В декабре 2014-го года театр празднует юбилей — 25 лет. За историю существования театр побывал с отдельными спектаклями и гастрольными поездками практически во всех крупных населённых пунктах Ленинградской области. На спектаклях театра в области ежегодно бывает до 15−17 тысяч человек. Особенной заботой театра пользуются дети, для которых ежегодно устраивается благотворительный театральный фестиваль «Дворцы Санкт-Петербурга — детям». Идея организации принадлежит Левшину М. А., поддержал идею Комитет по культуре Ленинградской области. Этот фестиваль давно стал значительным событием в культурной жизни Ленинградской области и города, а в 2013-м году вышел на международный уровень. Его основными зрителями и участниками были и остаются дети-сироты из детских домов и интернатов области. Цель этой благородной миссии — приобщение детей, лишённых родительской опеки, к театральной культуре.</p>\n<p>Важная сторона дарования М. А. Левшина — умение работать с актёром. Он создал по-настоящему актёрский театр. Именно в спектаклях театра «Комедианты» были замечены и приглашены в кино такие артисты, как Сергей Русскин (фильмы «Особенности национальной охоты», «Особенности национальной рыбалки» А. Рогожкина), Максим Сергеев («Молох» А. Сокурова, «Васька Немешаев»), Вадим Прохоров (фильмы А. Балабанова), Светлана Суханова («Пока цветет папоротник» и др.), Наталья Терехова («Ключи от счастья», «Петя великолепный» и др.) и многие другие.</p>\n<p>В Санкт-Петербургской театральной Академии доцент М. А. Левшин выпустил актёрский курс, воспитав молодое пополнение для труппы театра.</p>\n<p>За время существования театра режиссер выпустил более 20-ти спектаклей. «Страсти по-итальянски» (Э.де Филиппо), «Венецианский купец» (У. Шекспир), «Сирано де Бержерак» (Э. Ростан), «Татуированная роза» (Т. Уильямс), «Дурочка» (Лопе де Вега) и др. Но более подробно хочется остановиться на спектаклях режиссера, поставленных на материале русской классической литературы и драматургии.</p>\n<p>Безусловно, одна из самых значительных работ М. А. Левшина — спектакль по пьесе А. Н. Островского «Волки и овцы». Подлинная состоятельность спектакля основывалась на тщательно подобранном и воспитанном актёрском ансамбле. Именно в этом спектакле на театральном небосклоне Санкт-Петербурга зажглись такие звёзды, как Сергей Русскин, Нина Мещанинова, Галина Мочалова. Спектакль был показан на Всероссийском театральном фестивале, посвящённом 175-летию А. Н. Островского, и особо отмечен критикой.</p>\n<p>Спектакли М. А. Левшина отличаются выстроенностью и цельностью режиссёрской концепции. Пользуясь большим успехом у зрителей, они подолгу сохраняются в репертуаре, выдерживая сотни представлений. Многие постановки М. А. Левшина вызывают одинаковый интерес и у широкой публики, и у требовательных специалистов, и у критиков, приглашаются на самые престижные фестивали страны. Так спектакль «Граф Нулин» по одноимённой повести в стихах А. С. Пушкина стал участником трёх фестивалей Всероссийского значения => Пятого Пушкинского Всероссийского фестиваля в городе Пскове, посвящённого 200-летию поэта, Фестиваля Камерного искусства «Костомукша-99» и VI международного театрального фестиваля «Русская классика. Пушкин. Тургенев» в Орле.</p>\n<p>Театр «Комедианты» много и успешно гастролирует во многих регионах России, и повсюду постановки М. А. Левшина получают, без преувеличения можно сказать, восторженные отклики зрителей и прессы. Далеко неполный список городов, где побывали «Комедианты» займёт достаточно много места, поэтому перечислим лишь некоторые => Астрахань, Нижний Новгород, Самара, Архангельск, Тобольск, Ростов-на-Дону, Ставрополь, Великий Новгород, Чебоксары, Нижневартовск, Томск, Петрозаводск, Подпорожье, Луга, Череповец...</p>\n<p>Спектакль «Женитьба» по пьесе Н. В. Гоголя — поистине творческая удача театра и постановщика. В рецензиях говорится о том, как классическая комедия остро и смешно звучит сегодня в театре «Комедианты», о калейдоскопе комедийных образов, слаженности ансамбля, необычности решения сценического пространства. Спектакль, безусловно, стал событием и выдвинут на конкурс «Золотая маска». «Спектакль М. Левшина пленяет свежестью, таит новизну» — пишет критик И. Дорофеева в газете «Невское время».</p>\n<p>Еще одна выдающаяся работа режиссера — спектакль «Записные книжки Тригорина» (Т. Уильямс, интерпретация чеховской «Чайки»). К. Рубина (Москва) => «Опираясь в своей постановке прежде всего на актёрский ансамбль и сводя к минимуму внешние постановочные эффекты, режиссёр Михаил Левшин достигает изрядной степени психологической убедительности. Вникая в американскую версию известных драматических событий, он даёт свой внятный ответ на один из «проклятых» русских вопросов => «Почему застрелился Константин?» Спектакль заслужил «Золотой софит» за лучшую роль второго плана (з.а. России Виктор Костецкий).</p>\n<p>Оглянувшись на десятилетия, в течение которых М. А. Левшин руководит театром «Комедианты», режиссера смело можно назвать хранителем традиций русской театральной классической школы. В репертуаре театра преобладала и преобладает классика. Это и русский классический водевиль — «Барская спесь или Анютины глазки», «Беда от нежного сердца», и спектакль по ранним рассказам А. П. Чехова «В Париж!» (спектакль представил Санкт-Петербург на международном Интернет-фестивале «Театральная паутина» и одним из первых транслировался в сети в режиме on-line для многомиллионной российской аудитории.) Две последние премьеры театра — «Крепостная любовь» (пластическая драма по повести И. Тургенева «Муму») и музыкальная комедия «Лес» А. Островского уже заслужили внимание и уважение публики и критики. Спектакль «Крепостная любовь» в связи с особенностью жанра — действие без слов, а так же использовании в оформлении уникальных песен русского народного фольклора и картин великих русских художников, позволяет театру активно пропагандировать русское искусство за рубежом. Спектакль уже заслужил => «Приз зрительских симпатий — 2010» СПб общества «Театрал» в номинации — лучший спектакль года; Специальный приз экспертного совета высшей театральной премии Санкт-Петербурга «Золотой софит» сезона 2010 — 2011, Алена Азарова за роль Татьяны; специальный приз жюри на «13-ом Черноморском Международном Театральном Фестивале» в г. Трабзон (Турция) (2012 г.), диплом «За лучший актёрский ансамбль» на «1-ом фестивале театральных и музыкальных проектов камерных экспериментальных форм» в г. Выборге. (2013 г.)</p>\n<p>В ближайших планах режиссера премьера спектакля по рассказам Василия Шукшина. Шукшин, Островский, Тургенев, Чехов — это авторы, которые писали прежде всего о России, о том — кто мы такие? Это вопрос осознания себя, русского человека, с нашей историей, с нашими горестями и радостями. Что это такое — современный российский человек — важно понять это, и только осознав себя, пытаться научиться жить. Это то, что занимает режиссера сегодня, чему М. А. Левшин посвящает себя и деятельность своего театра.</p>', 'Заслуженный деятель искусств России', 'director', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(29, 'Наталья Боярёнок', 'natalya-boyaryonok', 'Боярёнок', 'Наталья', NULL, NULL, 'cooperate', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(30, 'Марк Гаврилов', 'mark-gavrilov', 'Гаврилов', 'Марк', '<p>Родился 17 апреля 1969 года.</p>\n<p>В 1991 году окончил ЛГИТМиК (СПБГАТИ) класс профессора А.И.Кацмана и В.М.Фильштинского.</p>\n<p>Работал в Театре Сатиры на Васильевском, в Театре На Литейном, Молодежном театре и в Студии &quot;АСБ«под управлением Алексея Янковского.</p>\n<p>С 2011 года актёр Санкт-Петербургского Государственного драматического театра «Комедианты».</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(31, 'Наталья Терехова', 'natalya-terekhova', 'Терехова', 'Наталья', '<p>Родилась 6 сентября 1982 года в Ленинграде. В 2004 году окончила СПбГАТИ, курс А.Куницына и Г.Барышевой. С 2001 года активно снимается в кино.</p>\n<p>В 2006 году принята в труппу Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'female', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27'),
(32, 'Николай Исполатов', 'nikolai-ispolatov', 'Исполатов', 'Николай', '<p>Родился 14 апреля 1951 г. в городе Ленинграде. Детство провел в городе Котельнич Кировской области. В 1974 году окончил Ленинградский театральный институт. Имеет диплом актёра театра и кино. С 1975 г.по1979 г. работал в ТЮЗах Красноярска и Орла. С 1983 актёр много снимается в кино, сыграл в фильмах режиссеров В. Трегубовича, А. Кайдановского, С. Ткачева. Николай Исполатов — актёр глубокого погружения в образ, и очень легко владеет жанром трагикомедии, так же владеет мастерством перевоплощения. Каждая его новая роль не похожа на предыдущую. С 2007 года — член Союза писателей России. Автор книги стихов &quot;Исповедь сокрушённого сердца&quot;и пьесы &quot;Угол Марата&quot;- журнал &quot;Балтийские сезоны&quot;.</p>\n<p>С 1990 года актёр Санкт-Петербургского государственного драматического театра «Комедианты».</p>', NULL, 'state', 'male', 1, '2015-02-25 09:06:27', '2015-02-25 09:06:27');
