        "content"       => "<p><img src=\"/storage/app/media/stories/alexandraperova_shukshinfest_20141102_teatrkomedianti-5229.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/alexandraperova_shukshinfest_20141102_teatrkomedianti-5246.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/alexandraperova_shukshinfest_20141102_teatrkomedianti-5270.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/alexandraperova_shukshinfest_20141102_teatrkomedianti-5254.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/alexandraperova_shukshinfest_20141102_teatrkomedianti-5365.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/safronov_melnikova_fest_2014_pressa.png\"></p>\n<p><img src=\"/storage/app/media/stories/shukshin_boyarskii.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/shukshin_pressa_prit_lev_fest.png\"></p>\n<p><img src=\"/storage/app/media/stories/shukshin_fest_2014_pressa.png\"></p>\n<p><img src=\"/storage/app/media/stories/shukshin_pressa_fest_20142.png\"></p>\n<p><img src=\"/storage/app/media/stories/15097271363_4d1d03b91c_b.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/alexandraperova_shukshinfest_20141102_teatrkomedianti-5320.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/15531216188_7511c168f5_b.jpg\"></p>\n<p><img src=\"/storage/app/media/stories/wpvwxxewsv0.jpg\"></p>\n<p class=\"published_at\"><small>09.11.2014</small></p>",
